INSERT INTO `task_data` VALUES ('1139', '2018-03-25 15:48:36', 'https://www.jianshu.com/p/9ea95925d065', '【武侠】飞鹤传情（90）', '<div class=\"show-content-free\">            <blockquote><p>第八十八章   彩虹由妻变妾</p></blockquote><p>婚礼简单明了。</p><p>一顶大花娇就将彩虹姑娘给抬进了肖家。</p><p>拜堂成亲一切简单有序的进行。</p><p>新婚之夜，彩虹姑娘见万顺喝醉了，便要珍儿代替自己坐在床前。</p><p>婚后第二天，万顺便发现睡在自己床上的竟然是珍儿，他立即明白了什么，匆匆穿了衣服当什么也不知道的出去。</p><p>肖毅和妻子等了两个多', '1521252660', null, '沐弘晨', 'http://static.shenjianshou.cn/image/1793887-ee2f7e0cb73f1c8e6fccc664fc68743c.jpg?realurl=https%3A%2F%2Fupload.jianshu.io%2Fusers%2Fupload_avatars%2F8665882%2F50774029-56ae-41fb-8395-3e12a9fe05d8.jpg', '[\"飞鹤传情\"]', '62', '2', '20', '1433');
