INSERT INTO `task_data` VALUES ('1549', '2018-03-25 15:36:57', 'https://www.jianshu.com/p/6a4e2baaf7be', '【纪实文学】同事老张（07）', '<div class=\"show-content-free\">            <p>        老张一家三口在小岛上过着平凡而恬静的生活，光阴荏苒，不觉儿子下学期要六年级了。</p><p>        可是，老张的平静生活被一个突发事件打破，而且留下了终生难忘的记忆。<br></p><p>        老张因出海钓鱼，渔船被风浪颠覆，经历了一次生死考验，这一次却经历了一次生死离别般的煎熬。</p><p>        那是八三年五月份的一个星期一的早晨，老张是班主任，七点许，老张到班上例行', '1521413340', 'http://static.shenjianshou.cn/image/1793887-4bd6e55128844b76ac08d1e68daaa059.jpeg?realurl=https%3A%2F%2Fupload-images.jianshu.io%2Fupload_images%2F6986039-4eb2aca7ec0eb4dc.jpeg', '空谷孤松', 'http://static.shenjianshou.cn/image/1793887-a02df4c767da62617516ad24acbf09f4.jpg?realurl=https%3A%2F%2Fupload.jianshu.io%2Fusers%2Fupload_avatars%2F6986039%2F77083b62-c5a3-4d25-82a4-fc0ba8af735e.jpg', '[\"纪实文学\"]', '109', '10', '16', '1237');
