INSERT INTO `task_data` VALUES ('1739', '2018-03-25 15:31:42', 'https://www.jianshu.com/p/795f1209bd13', '【武侠】飞鹤传情（92）', '<div class=\"show-content-free\">            <blockquote><p>第九十章     秘道之密</p></blockquote><p>闻人劲松依然在发着他莫名的大火，闻人祎依然垂首恭听。</p><p>从小到大，他是第一次见父亲发这样大的火。</p><p>即使在他十六岁那年，他一剑削掉了青年剑客罗文的一条手臂，在鹤鸣庄的练武场，公开挨了一百二十多鞭的时候，都没今天的火大。</p><p>今天父亲的手中虽然没有鞭子，可是他的话句句都像是根鞭子，鞭笞着闻人祎的心。<', '1521505920', null, '沐弘晨', 'http://static.shenjianshou.cn/image/1793887-ee2f7e0cb73f1c8e6fccc664fc68743c.jpg?realurl=https%3A%2F%2Fupload.jianshu.io%2Fusers%2Fupload_avatars%2F8665882%2F50774029-56ae-41fb-8395-3e12a9fe05d8.jpg', '[\"飞鹤传情\"]', '41', '7', '10', '1888');
