INSERT INTO `task_data` VALUES ('1770', '2018-03-25 15:30:46', 'https://www.jianshu.com/p/c0373e106189', '【纪实文学】同事老张（08）', '<div class=\"show-content-free\">            <p>        小岛上就一个卫生院，在乡政府所在地，就在学校旁边。老张望着昏迷不醒的小强心急如焚，是责任心和义不容辞的使命感，是精神的力量支撑着老张忍着伤痛、饥饿和疲惫，蹒跚在这小岛的碎石小路上。<br></p><p>        记得还是67年的文革大串联，跟着一帮狂热的红卫兵小将从长春去天安门接受总司令的检阅，绿皮货车停在通州进不去了，为了第二天赶上检阅，十几个人走了半天一夜，脚上满是血泡，还把解放鞋挤掉了，', '1521504360', 'http://static.shenjianshou.cn/image/1793887-c5c9ecbddff497a87ccccc1409acb512.jpeg?realurl=https%3A%2F%2Fupload-images.jianshu.io%2Fupload_images%2F6986039-d8503fd304da5fe4.jpeg', '空谷孤松', 'http://static.shenjianshou.cn/image/1793887-a02df4c767da62617516ad24acbf09f4.jpg?realurl=https%3A%2F%2Fupload.jianshu.io%2Fusers%2Fupload_avatars%2F6986039%2F77083b62-c5a3-4d25-82a4-fc0ba8af735e.jpg', '[\"纪实文学\"]', '104', '5', '12', '1046');
