INSERT INTO `task_data` VALUES ('1827', '2018-03-25 15:29:13', 'https://www.jianshu.com/p/2d6419d17a4d', '【奇幻】契魂人（6）', '<div class=\"show-content-free\">            <p>第二卷 第三章</p><p>    船，孤独的停泊在岛的入口。平日里显得庞大的身躯在这时竟是那么的不起眼，仿佛是被其身后那宏伟，瑰丽的岛屿给镇住了，害怕的缩在一旁，不敢发出半点声音。船上的人们早已走下船去，全副武装的踏入这块几乎无人问津的土地上。</p><p>    船身掀起两道炫目的浪花，船头豁开平静的海面，并无宽余的航道上，教授一行人所预备的救生船正拘谨地前行。眼看着可供行驶的道路越来越少，可前方错综复杂且令人', '1521515940', null, '夕山尧', 'http://static.shenjianshou.cn/image/1793887-c8f223b9e5ae3d0ec45a8e549415bba8.jpg?realurl=https%3A%2F%2Fupload.jianshu.io%2Fusers%2Fupload_avatars%2F9267840%2F0590462f-4803-4f33-9eb6-6b8f7f94239f.jpg', '[\"契魂人\"]', null, null, null, '1325');
