INSERT INTO `task_data` VALUES ('2740', '2018-03-25 15:03:38', 'https://www.jianshu.com/p/f72c69af9c46', '【玄幻】剑破云天（13）', '<div class=\"show-content-free\">            <p>第十三章   苏醒</p><p>    李奕天也不知道自己昏迷了多久，眼前只有一团淡金色的龙形印记在浮动着，印记缓缓旋转，自己看着一缕缕的金色光芒从印记上飘到乳白色缩小版的一个小人上，这小人面相与自己极其相似，但眼睛是紧闭的，一丝丝金色光丝飘到小人身上，没有滑落，反而飞速融入小人躯体内，李奕天看着自己体内的变化，心中有些着急，却又无能为力，看的时间长了，也只能无可奈何的看着金色光团，一丝丝的融入小人身体之中，周围是', '1521852480', null, '迎风的溯雪', 'http://static.shenjianshou.cn/image/1793887-c6286ed0c5b45ce3e81edbe55ce9512e.jpg?realurl=https%3A%2F%2Fupload.jianshu.io%2Fusers%2Fupload_avatars%2F10898101%2Fa17f03a5-5a84-4048-bfc2-288ae7778a85.jpg', '[\"剑破云天\"]', '2', null, null, '1247');
