INSERT INTO `task_data` VALUES ('2845', '2018-03-25 15:00:49', 'https://www.jianshu.com/p/04f80ea4bbc2', '【乡土】秀姑（10）', '<div class=\"show-content-free\">            <p>第十章  尾声<br></p><p>          时间翻到一九四九年七月，扶眉战役打响，第一野战军在司令员彭德怀指挥下由第一和第十八兵团沿渭河两岸由东向西推进歼敌，第二兵团在兴平、武功一带西渡漆水，切敌退路。第十九兵团骑兵进攻乾县、礼泉之马步芳部，第六十一军警备西安，准备打援。十一日两军激战漆水河，河道为人马尸身所堵，几欲断流，十二日在渭河滩围歼国民党第十八兵团及第三十八、六十五和第一一九军，河水因之变赤。是', '1521880560', null, '辛勤的老三', 'http://static.shenjianshou.cn/image/1793887-ce76b79fdb6f8463dc1c2ffbc0e8f580.jpg?realurl=https%3A%2F%2Fupload.jianshu.io%2Fusers%2Fupload_avatars%2F9686524%2F22e04ec3-d00a-4732-b037-2da6c4751c13.jpg', '[\"小说连载\"]', '6', null, '1', '396');
